import logo from './logo.svg';
import './App.css';
import React, {Component} from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity, Dimensions,ImageBackground, ScrollView
} from 'react-native';
import { FacebookShareButton, FacebookIcon } from "react-share";
import WheelOfFortune from './Components/WheelOfFortune';
const participants = [
  '10% Discount',
  '50% Discount on Accessories',
  '5.1 Home Theatre',
  'NeckBand',
  'None',
  'SmartBand',
  'HeadSet',
  'BackCase',
  'None',

];

class App extends Component  {
  constructor(props) {
    super(props);

    this.state = {
      winnerValue: null,
      winnerIndex: null,
      started: false,
    };
    this.child = null;
  }

  buttonPress = () => {
    this.setState({
      started: true,
    });
    this.child._onPress();
  };
  render(){
  const wheelOptions = {
  rewards: participants,
  knobSize: 30,
  borderWidth: 5,
  borderColor: '#fff',
  innerRadius: 30,
  duration: 6000,
  backgroundColor: 'transparent',
  textAngle: 'horizontal',
  knobSource: require('./assets/images/knob.png'),
  onRef: ref => (this.child = ref),
};
  return (
    <View style={styles.container}>
           <ImageBackground 
              source={{uri:'https://cdn4.vectorstock.com/i/1000x1000/18/08/fireworks-background-for-diwali-festival-vector-14301808.jpg'}} 
              style={[styles.fixed, styles.containter2]}>  

      <View style={styles.inputLayer}>
            <Text style={styles.inputFieldStyle}>FACEBOOK ID</Text>
            <Text style={styles.inputFieldStyle}>NANDY AKSHARA</Text>

          </View>
          <View style={styles.spinTitle}>
            {!this.state.started && (
              <View style={styles.startButtonView}>
                 <TouchableOpacity
                  onPress={() => this.buttonPress()}
                  style={styles.startButton}>
                  <Text style={styles.startButtonText}>Spin to win!</Text>
                </TouchableOpacity> 
              </View>
            )}
            {this.state.winnerIndex != null && (
              <View style={styles.winnerView}>
                <Text style={styles.winnerText}>
                  You win {participants[this.state.winnerIndex]}
                </Text>
                <TouchableOpacity
                  onPress={() => {
                    this.setState({ winnerIndex: null });
                    this.child._tryAgain();
                  }}
                  style={styles.tryAgainButton}>
                  <Text style={styles.tryAgainText}>TRY AGAIN</Text>
                </TouchableOpacity> 

              </View>
            )}

          </View>
          <ScrollView>
            <View style={styles.wheelOfFortune}>


              <WheelOfFortune
                options={wheelOptions}
                getWinner={(value, index) => {
                  this.setState({ winnerValue: value, winnerIndex: index });
                }}
              />

            </View>
          </ScrollView>
          <FacebookShareButton
            url={"http://www.camperstribe.com"}
            quote={"@Poorliya - Mobile World is yours to explore"}
            hashtag="#Poorliya">

            <FacebookIcon size={36} />
          </FacebookShareButton>
</ImageBackground>
</View> 
  );
}

}

export default App;
const styles = StyleSheet.create({
  wheelOfFortune: {
    alignItems: 'center',
   justifyContent: 'center',
  },
  startButtonView: {
   position: 'absolute',
  },
  startButton: {
    backgroundColor: 'rgba(0,0,0,.5)',
    marginTop: 50,
    padding: 5,
  },
  startButtonText: {
    fontSize: 50,
    color: '#fff',
    fontWeight: 'bold',
  },
  winnerView: {
    position: 'absolute',
    justifyContent: 'center',
    alignItems: 'center',
  },
  // tryAgainButton: {
  //  padding: 10,
  // },
  winnerText: {
    fontSize: 30,
  },
  tryAgainButton: {
    padding: 5,
    backgroundColor: 'rgba(0,0,0,0.5)',
  },
  tryAgainText: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#fff',
  },
  inputLayer:{
    flex:0.2,
    flexDirection:'row',
marginTop:120
  },
  container:{
flex:1,
backgroundColor: '#E74C3C'
  },
  spinTitle:{
    flex:0.2,
    flexDirection:'row',
padding:20 ,
alignItems: 'center',
justifyContent: 'center',
  },
  image: {
    width: 300,
    height: 200,
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: '1.5rem'
  },
  containter2: {
    width: Dimensions.get("window").width, //for full screen
    height: Dimensions.get("window").height //for full screen
  },
  fixed: {
   position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0
  },
  inputFieldStyle:{
    fontSize: 20,
    fontWeight: 'bold',
    color: '#fff',
    flex:1,
justifyContent:"center" 
 }
});
